import './App.css';
import { TextField } from '@mui/material';
import { Button } from '@mui/material';

function App() {

  //js code
  const validate = (e) => {
    e.preventDefault(); // Prevent the default form submission behavior

    const email = e.target.email.value;
    const password = e.target.password.value;
    const confirmPassword = e.target.confirmPassword.value;

    if (!email) {
      alert('Please enter an email address.');
    } else if (!isValidEmail(email)) {
      alert('Please enter a valid email address.');
    } else if (!password) {
      alert('Please enter a password.');
    } else if (password.length < 6) {
      alert('Password must be at least 6 characters long.');
    } else if (password !== confirmPassword) {
      alert('Passwords do not match.');
    } else {
      // Form is valid, you can submit it or perform further actions here
      // For example, you can send the data to a server or update state in a React component
      // Submit the form or take further action
    }
  };

  // Function to validate email format
  function isValidEmail(email) {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  }

  return (
    <div>
      <div className='container '>
        <div className="row">
          <div className="col-md-4"></div>
          <div className="col-md-4 mt-5">

            <form className='mt-5 border border-dark rounded p-5 bg-light '>
              <h3 className='text-center '>Register</h3>
              <div className="mb-3">
                <TextField onChange={(e)=>validate(e)} className='w-100' id="standard-basic" label="Email" variant="standard" />
                <div id="emailHelp" className="form-text ">We'll never share your email with anyone else.</div>
              </div>
              <div className="mb-3">
                <TextField onChange={(e)=>validate(e)} className='w-100' id="standard-basic" label="Password" variant="standard" type='password'/>
              </div>
              <div className="mb-3">
                <TextField onChange={(e)=>validate(e)} className='w-100' id="standard-basic" label="Confirm Password" variant="standard" type='password'/>
              </div>

              <p>Already have an account? <a href="">Login</a></p>


              <Button type="submit" className='bg-success w-50 mx-auto' variant="contained">Sign Up</Button>

            </form>
          </div>
          <div className="col-md-4"></div>
        </div>
      </div>
    </div>
  );
}

export default App;;
